class LabelPointData(object):

    def __init__(self, X, y):
        self.X = X
        self.y = y
        self.labeled_point_data = []
        pass

    def covert(self):
        index = 0
        for label in self.y:
            labeled_point = LabeledPoint(label, self.X[index])
            self.labeled_point_data.append(labeled_point)
            index += 1
        pass

